import React, { useState } from "react";
import {
  Button,
  Grid,
  Alert,
  List,
  ListItem,
  ListItemText,
  Typography,
} from "@mui/material";
import FileUploadOutlinedIcon from "@mui/icons-material/FileUploadOutlined";
import PlantResults from "./PlantResults";
import NavBar from "./components/Navbar";
import { useAuth } from "./contexts/AuthContext";
import { Link, useNavigate } from "react-router-dom";

import "./userDash.css";

export default function UserDash() {
  const [selectedImages, setSelectedImages] = useState([]);
  const captionRef = useRef(null);
  //function to upload through click
  const uploadPost = async () => {
    const docRef = await addDoc(collection(db, "upload"), {
      //creating firestore data structure
      caption: captionRef.current.value,
      timestamp: serverTimestamp(),
    });
    await Promise.all(
      selectedImages.map((image) => {
        //create images path
        const imageRef = ref(storage, `upload/${docRef.id}/${image.path}`);
        uploadBytes(imageRef, image, "data_url").then(async () => {
          const downloadURL = await getDownloadURL(imageRef);
          await updateDoc(doc(db, "upload", docRef.id), {
            image: arrayUnion(downloadURL),
          });
        });
      })
    );
    captionRef.current.value = "";
    setSelectedImages([]);
    console.log("uploaded!");
  };

  //function to handle drop activity
  const onDrop = useCallback((acceptedFiles) => {
    setSelectedImages(
      acceptedFiles.map((file) =>
        Object.assign(file, {
          preview: URL.createObjectURL(file),
        })
      )
    );
  }, []);
  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop });
  const selected_image = selectedImages.map((file) => (
    <div>
      <img src={file.preview} style={{ width: "200px" }} alt="" />
    </div>
  ));


  return (
    <>
      {" "}
      <NavBar typeOfBar ="dashNav" />

      <Grid container>
      <Grid item width="30%" sx={{ border: "5px solid cyan" }}>
          <div>
            <div {...getRootProps()}>
              <input {...getInputProps()} />
              {isDragActive ? (
                <div
                  style={{
                    border: "5px dashed black",
                    borderRadius: "10%",
                    paddingBottom: "15em",
                    paddingTop: "10em",
                    color: "#000000",
                    background: "#ffffff",
                    opacity: "90%",
                  }}
                >
                  <h3
                    style={{
                      padding: "1.3em",
                      fontSize: "1.5rem",
                    }}
                  >
                    Drop the files here ...
                  </h3>
                </div>
              ) : (
                <div
                  style={{
                    border: "5px dashed black",
                    borderRadius: "10%",
                    paddingBottom: "15em",
                    paddingTop: "10em",
                    color: "#000000",
                    background: "#ffffff",
                    opacity: "70%",
                  }}
                >
                  <h3
                    style={{
                      padding: "1.3em",
                      fontSize: "1.5rem",
                    }}
                  >
                    Drag 'n' drop some files here, or click to select files
                  </h3>
                  <div
                    style={{

                      display: "flex",
                      justifyContent: "center",
                    }}
                  >
                    <FileUploadOutlinedIcon fontSize="large" />
                  </div>
                </div>
              )}
            </div>
          </div>
        </Grid>
        <Grid
          item
          lg={5}
          sx={{
            padding: "2em",
          }}
        >
          <Typography
            variant="h3"
            sx={{
              color: "#ffffff",
              border: "1px solid pink",
              padding: "1em",
            }}
          >
            Upload a picture of a crop to get more info
          </Typography>
          <div className="predict_btn">
            <PlantResults />
          </div>

        </Grid>
        <Grid item>
          <Button variant="outlined" onClick={handleLogout}>
            {" "}
            LogOut{" "}
          </Button>
          <Link to="/update-profile">
            <Button variant="outlined">Update profile</Button>
          </Link>
        </Grid>
      </Grid>
    </>
  );
}
