import React, { useState } from "react";
import {
  Button,
  Grid,
  Alert,
  List,
  ListItem,
  ListItemText,
  Typography,
} from "@mui/material";
import FileUploadOutlinedIcon from "@mui/icons-material/FileUploadOutlined";
import PlantResults from "./PlantResults";
import NavBar from "./components/Navbar";
import { useAuth } from "./contexts/AuthContext";
import { Link, useNavigate } from "react-router-dom";

import "./userDash.css";

export default function UserDash() {
  const [error, setError] = useState("");
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();

  //setting drag state
  const [dragActive, setDragActive] = useState(false);

  //drag events
  const handleDrag = function (e) {
    e.preventDefault();
    e.stopPropagation();

    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
      console.log("Hey im here")
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };
  // triggers when file is dropped
  const handleDrop = function (e) {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      console.log("upload")
    }
  };

  async function handleLogout() {
    setError(" ");

    try {
      await logout();
      navigate("/login");
    } catch {
      setError("Failed to logout");
    }
  }

  return (
    <>
      {" "}
      <NavBar />
      {error && <Alert severity="error"> {error} </Alert>}
      <Grid
        container
        sx={{
          padding: "10em 7em 4em",
        }}
      >
        <Grid
          item
          width={"50%"}
          sx={{
            padding: "2em",
          }}
        >
          <div className="user_widget">
            <div className="upload_icon">
              <FileUploadOutlinedIcon
                fontSize="large"
                sx={{
                  fontSize: "4em",
                }}
              />
            </div>
            <form id="form-file-upload" onDragEnter={handleDrag}>
              <input type="file" id="input-file-upload" multiple={false} />
              <div className="widget_text">
               {dragActive && (<div
                    id="drag-file-element"
                    onDragEnter={handleDrag}
                    onDragLeave={handleDrag}
                    onDragOver={handleDrag}
                    onDrop={handleDrop}
                  ></div>)} 
                    <Typography variant="h5">

                  Drag and drop your file here
                </Typography>

              </div>
              <div className="upload_btn">
                <Button variant="outlined">Upload a file</Button>
              </div>
            </form>
          </div>
        </Grid>
        <Grid
          item
          width={"50%"}
          sx={{
            padding: "2em",
          }}
        >
          <Typography
            variant="h3"
            sx={{
              color: "#ffffff",
              display: "flex",
              // border: "1px solid pink",
              padding: "1em",
              justifyContent: "center",
            }}
          >
            Upload a picture of a crop to get more info
          </Typography>
          <div className="predict_btn">
            <PlantResults />
          </div>
          {/* 
            <Button
              variant="contained"
              size="large"
              sx={{
                color: "#ffffff",
                background: "#308335",
                padding: "1em",
              }}
            >
              CHECK
            </Button>
          </div> */}
        </Grid>
        <Grid item>
          <Button variant="outlined" onClick={handleLogout}>
            {" "}
            LogOut{" "}
          </Button>
          <Link to="/update-profile">
            <Button variant="outlined">Update profile</Button>
          </Link>
        </Grid>
      </Grid>
    </>
  );
}
