import React, { useEffect, useState } from "react";
import { useAuth } from "./contexts/AuthContext";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
} from "@mui/material";
import {
  get,
  child,
  getDatabase,
  query,
  ref,
  set,
  orderByChild,
  remove,
  equalTo,
  onValue,
  orderByKey,
} from "firebase/database";
import Paper from "@mui/material/Paper";

export default function ManageUsers(usrData) {
  let rows = [];
  const [show, setShow] = useState()

  //functin to read and display users
  function readUser() {
    const usrQueryRef = query(ref(getDatabase(), 'users'))

    onValue(usrQueryRef,(snapshot)=>{
     setUsrData(snapshot.val())
      console.log(snapshot.val())

    })

    console.log(usrData)

  }


  useEffect(() => {
    for(let x in usrData){
      usrData[x].userName = x;
      rows.push(usrData[x])
    }
    setShow(rows)
    console.log(show)


  }, []);

  return (
    <div>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell align="center">User ID</TableCell>
              <TableCell align="center">User Name</TableCell>
              <TableCell align="center">User email</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row) => (
              <TableRow key={row.userId}>
                <TableCell align="center">{row.userId}</TableCell>
                <TableCell align="center">{row.userName}</TableCell>
                <TableCell align="center">{row.email}</TableCell>
                <TableCell align="center"></TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
}
