import React, { useCallback, useRef, useState } from "react";
import { useDropzone } from "react-dropzone";
import { db, storage } from "./firebase";
import {
  addDoc,
  arrayUnion,
  collection,
  doc,
  serverTimestamp,
  updateDoc,
} from "firebase/firestore";
import { ref, getDownloadURL, uploadBytes } from "@firebase/storage";
import { Grid } from "@mui/material";
import NavBar from "./components/Navbar";

export default function Predict() {
  const [selectedImages, setSelectedImages] = useState([]);
  const captionRef = useRef(null);
  //function to upload through click
  const uploadPost = async () => {
    const docRef = await addDoc(collection(db, "upload"), {
      //creating firestore data structure
      caption: captionRef.current.value,
      timestamp: serverTimestamp(),
    });
    await Promise.all(
      selectedImages.map((image) => {
        //create images path
        const imageRef = ref(storage, `upload/${docRef.id}/${image.path}`);
        uploadBytes(imageRef, image, "data_url").then(async () => {
          const downloadURL = await getDownloadURL(imageRef);
          await updateDoc(doc(db, "upload", docRef.id), {
            image: arrayUnion(downloadURL),
          });
        });
      })
    );
    captionRef.current.value = "";
    setSelectedImages([]);
    console.log("uploaded!");
  };

  //function to handle drop activity
  const onDrop = useCallback((acceptedFiles) => {
    setSelectedImages(
      acceptedFiles.map((file) =>
        Object.assign(file, {
          preview: URL.createObjectURL(file),
        })
      )
    );
  }, []);
  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop });
  const selected_image = selectedImages.map((file) => (
    <div>
      <img src={file.preview} style={{ width: "200px" }} alt="" />
    </div>
  ));

  return (
    <div>
      <NavBar typeOfBar="mainNav" />
      <Grid container  sx={{ border: "4px solid orange" }}>
        <Grid item lg={6} sx={{ border: "5px solid cyan", padding: "2em" }}>
          <div >
          <div {...getRootProps()}>
            <input {...getInputProps()} />
            {isDragActive ? (
              <div style={{ border:"4px dotted red", paddingBottom:"30em", width:"60%", color:"#ffffff"}}>
                <p>Drop the files here ...</p>
              </div>
            ) : (
              <p>Drag 'n' drop some files here, or click to select files</p>
            )}
            </div>
          </div>
        </Grid>
        <Grid item lg={2} sx={{ border: "2px solid red" }}>
          <input ref={captionRef} type="text" placeholder="Enter cap" />
          <button onClick={uploadPost}>send</button>
        </Grid>
        <Grid item lg={2} sx={{ border: "2px solid red" }}>
          {selected_image}
        </Grid>
      </Grid>
    </div>
  );
}
